journey_id = #{journeyId}, project_id = #{projectId}, regimen_id = #{regimenId}, approver_name = #{approverName}, cost_center = #{costCenter}, state = #{state}, reason = #{reason}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where apply_id = #{applyId}
