select.where:
state != 'S444'
 and city = #{city}
 and leader = #{leader}
 and company_id = #{companyId}
 and parent_car_group_id = #{parentCarGroupId}

mapper.update:
update car_group_info
city = #{city}, car_group_name = #{carGroupName}, car_group_code = #{carGroupCode}, company_id = #{companyId}, parent_car_group_id = #{parentCarGroupId}, leader = #{leader}, longitude = #{longitude}, latitude = #{latitude}, short_address = #{shortAddress}, full_address = #{fullAddress}, telephone = #{telephone}, state = #{state}, allow_outer_dispatch = #{allowOuterDispatch}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where car_group_id = #{carGroupId}

mapper.select:
select c.car_group_id as carGroupId, c.car_group_name as carGroupName, c.car_group_code as carGroupCode, c.company_id as companyId, e1.dept_name as ownerCompanyName, e1.dept_id as ownerOrg, c.state,c.city,ch.SHORT_NAME as cityName from car_group_info c left join ecmp_org e1 on e1.dept_id = c.company_id left join china_city ch on ch.CITY_CODE = c.city
c.state != 'S444'
and (c.car_group_name like concat('%',#{search},'%') or c.car_group_code like concat('%',#{search},'%'))
and c.state = #{state}
and c.company_id = #{deptId} and c.parent_car_group_id = 0
and c.parent_car_group_id = #{carGroupId}
and c.company_id = #{companyId}
order by c.car_group_name

select car_group_name as carGroupName, telephone as phone from car_group_info where car_group_id in
#{groupId}
and state = 'Y000'

select.where:
state = 'Y000'
 and city = #{city}
 and leader = #{leader}
 and company_id = #{companyId}

mapper.select:
