select.where:
state != 'S444'
 and city = #{city}
 and owner_org = #{ownerOrg}
 and leader = #{leader}
 and company_id = #{ownerCompany}

mapper.update:
update car_group_info
city = #{city}, car_group_name = #{carGroupName}, car_group_code = #{carGroupCode}, owner_org = #{ownerOrg}, parent_car_group_id = #{parentCarGroupId}, leader = #{leader}, longitude = #{longitude}, latitude = #{latitude}, short_address = #{shortAddress}, full_address = #{fullAddress}, telephone = #{telephone}, state = #{state}, allow_outer_dispatch = #{allowOuterDispatch}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where car_group_id = #{carGroupId}

mapper.select:
select car_group_name as carGroupName, telephone as phone from car_group_info where car_group_id in
#{groupId}
and state = 'Y000'

