mapper.select:
select cqi.collection_id collectionId, cqi.company_id companyId, cqi.collection_number collectionNumber, cqi.collection_end_time collectionEndTime, cgi.car_group_name deptName, cgi.car_group_id carGroupId, cqi.begin_date beginDate, cqi.end_date endDate, cqi.state state, eu.nick_name verifierName, cqi.create_time createTime from collection_quittance_info cqi left join car_group_info cgi on cgi.car_group_id = cqi.car_group_id left join ecmp_user eu on eu.user_id = cqi.verifier where 1=1
cqi.company_id=#{companyId}
and cgi.car_group_name like concat('%',#{deptName},'%')
and cqi.collection_number like concat('%',#{collectionNumber},'%')
and cqi.state =#{state}

mapper.update:
update collection_quittance_info
state = #{state}, verifier = #{verifier}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where collection_id=#{collectionId}
