mapper.update:
update cost_config_city_info
cost_id = #{costId}, city_code = #{cityCode}, city_name = #{cityName}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select count(1) from cost_config_city_info
where cost_id = #{costId}
and city_code!=#{cityCode}
