mapper.update:
update cost_config_info
company_id = #{companyId}, cost_config_name = #{costConfigName}, service_type = #{serviceType}, rent_type = #{rentType}, start_price = #{startPrice}, combos_price = #{combosPrice}, combos_mileage = #{combosMileage}, combos_times = #{combosTimes}, beyond_price_every_km = #{beyondPriceEveryKm}, beyond_price_every_minute = #{beyondPriceEveryMinute}, wait_price_erery_minute = #{waitPriceEreryMinute}, state = #{state}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where cost_id = #{costId}

mapper.select:
select c.cost_id as costId, cc.id as costConfigCityId, c.company_id as companyId, c.cost_config_name as costConfigName, c.rent_type as rentType, cc.city_name as cityName, cc.city_code as cityCode, c.service_type as serviceType, c.create_time as createTime, c.update_time as updateTime from cost_config_info c left join cost_config_city_info cc on cc.cost_id=c.cost_id left join cost_config_car_type_info ct on ct.cost_id=c.cost_id
c.cost_config_name like CONCAT('%',#{costConfigName},'%')
and cc.city_code=#{cityCode}
and c.service_type=#{serviceType}
and c.company_id=#{companyId}
and c.rent_type=#{rentType}
and ct.car_type_id=#{carTypeId}
group by c.cost_id,c.company_id,c.cost_config_name,cc.city_name,c.service_type

select count(1)
  from cost_config_info c
  left join cost_config_city_info  cc on cc.cost_id=c.cost_id
  left join cost_config_car_type_info ct on ct.cost_id=c.cost_id
  where   cc.city_code=#{cityCode} and c.service_type=#{serviceType}
  
      and c.rent_type=#{rentType}
  
 and ct.car_type_id in
 
    #{item.carTypeId}

select c.cost_id as costId, c.company_id as companyId, c.cost_config_name as costConfigName, cc.city_code as cityCode, cc.city_name as cityName, c.service_type as serviceType, c.rent_type as rentType, c.start_price as startPrice, c.combos_price as combosPrice, c.combos_mileage as combosMileage, c.beyond_price_every_km as beyondPriceEveryKm, c.beyond_price_every_minute as beyondPriceEveryMinute, c.wait_price_erery_minute as waitPriceEreryMinute, c.create_time as createTime, c.update_by as updateTime from cost_config_info c left join cost_config_city_info cc on cc.cost_id=c.cost_id left join cost_config_car_type_info ct on ct.cost_id=c.cost_id
and c.cost_id =#{costId}
and cc.city_code=#{cityCode}
and c.service_type=#{serviceType}
and c.company_id=#{companyId}
and c.rent_type=#{rentType}
and ct.car_type_id=#{carTypeId}
group by c.company_id,cc.city_name,c.service_type
