mapper.update:
update cost_config_info
company_id = #{companyId}, cost_config_name = #{costConfigName}, service_type = #{serviceType}, rent_type = #{rentType}, start_price = #{startPrice}, combos_price = #{combosPrice}, combos_mileage = #{combosMileage}, combos_times = #{combosTimes}, beyond_price_every_km = #{beyondPriceEveryKm}, beyond_price_every_minute = #{beyondPriceEveryMinute}, wait_price_erery_minute = #{waitPriceEreryMinute}, state = #{state}, car_group_user_mode = #{carGroupUserMode}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where cost_id = #{costId}

mapper.select:
select c.cost_id as costId, cc.id as costConfigCityId, c.company_id as companyId, c.cost_config_name as costConfigName, c.rent_type as rentType, c.car_group_user_mode as carGroupUserMode, cc.city_name as cityName, cc.city_code as cityCode, c.service_type as serviceType, cccgi.car_group_id carGroupId, (select car_group_name from car_group_info cgi where cgi.car_group_id =cccgi.car_group_id ) carGroupName, c.create_time as createTime, c.update_time as updateTime from cost_config_info c left join cost_config_city_info cc on cc.cost_id=c.cost_id left join cost_config_car_group_info cccgi on cccgi.cost_id=c.cost_id
and c.cost_config_name like CONCAT('%',#{costConfigName},'%')
and cc.city_code=#{cityCode}
and c.service_type=#{serviceType}
and c.company_id=#{companyId}
and cccgi.car_group_id=#{carGroupId}
and c.rent_type=#{rentType}
-- group by c.cost_id,c.company_id,c.cost_config_name,cc.city_name,c.service_type order by IFNULL(c.update_time,c.create_time) desc

select count(1)
  from cost_config_info c
  left join cost_config_city_info  cc on cc.cost_id=c.cost_id
  left join cost_config_car_type_info ct on ct.cost_id=c.cost_id
  where   cc.city_code=#{cityCode} and c.service_type=#{serviceType}

      and c.rent_type=#{rentType}

 and ct.car_type_id = #{carTypeId}

select c.cost_id as costId, c.company_id as companyId, c.cost_config_name as costConfigName, cc.city_code as cityCode, cc.city_name as cityName, c.service_type as serviceType, c.car_group_user_mode as carGroupUserMode, c.rent_type as rentType, c.start_price as startPrice, c.combos_price as combosPrice, c.combos_times as combosTimes, c.combos_mileage as combosMileage, c.beyond_price_every_km as beyondPriceEveryKm, c.beyond_price_every_minute as beyondPriceEveryMinute, c.wait_price_erery_minute as waitPriceEreryMinute from cost_config_info c left join cost_config_city_info cc on cc.cost_id=c.cost_id left join cost_config_car_type_info ct on ct.cost_id=c.cost_id left join cost_config_car_group_info cccgi on c.cost_id = cccgi.cost_id
and c.cost_id =#{costId}
and cc.city_code=#{cityCode}
and c.service_type=#{serviceType}
and c.company_id=#{companyId}
and c.rent_type=#{rentType}
and ct.car_type_id=#{carTypeId}
and c.car_group_user_mode=#{carGroupUserMode}
and cccgi.car_group_id=#{carGroupId}
group by c.company_id,cc.city_name,c.service_type

select count(*)
    from cost_config_info c
    left join cost_config_city_info  cc on cc.cost_id=c.cost_id
    left join cost_config_car_group_info  cccgi on cccgi.cost_id=c.cost_id


        and c.cost_config_name like  CONCAT('%',#{costConfigName},'%')


        and cc.city_code=#{cityCode}


        and c.service_type=#{serviceType}


        and c.company_id=#{companyId}


        and cccgi.car_group_id=#{carGroupId}


        and c.rent_type=#{rentType}

SELECT cci.*, cccg.car_group_id, chc.city_code, chc.city_name, ecti.`level` FROM cost_config_info cci LEFT JOIN cost_config_car_group_info cccg ON cccg.cost_id=cci.cost_id LEFT JOIN cost_config_city_info ccci ON ccci.cost_id=cci.cost_id LEFT JOIN cost_config_car_type_info cccti ON cccti.cost_id=cci.cost_id LEFT JOIN china_city chc ON chc.city_code=ccci.city_code LEFT JOIN enterprise_car_type_info ecti ON ecti.car_type_id=cccti.car_type_id LEFT JOIN car_group_info cgi ON cgi.car_group_id=cccg.car_group_id
1=1 AND cci.state='Y000' AND ecti.`status`='S000'
AND cccg.car_group_id = #{carGroupId}
AND chc.city_code=#{cityCode}
AND cci.company_id=#{companyId}
