mapper.update:
update driver_info
user_id = #{userId}, driver_name = #{driverName}, country = #{country}, nation = #{nation}, id_card = #{idCard}, birthday = #{birthday}, gender = #{gender}, mobile = #{mobile}, address = #{address}, emergency_contact_a = #{emergencyContactA}, emergency_contact_b = #{emergencyContactB}, emergency_contact_c = #{emergencyContactC}, it_is_full_time = #{itIsFullTime}, license_type = #{licenseType}, license_issue_date = #{licenseIssueDate}, license_init_issue_date = #{licenseInitIssueDate}, license_number = #{licenseNumber}, license_archives_number = #{licenseArchivesNumber}, license_expire_date = #{licenseExpireDate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where driver_id = #{driverId}

mapper.select:
SELECT a.driver_name AS driverName, a.driver_id AS driverId, a.user_id AS userId, a.mobile as mobile, a.state AS state, a.it_is_full_time AS itIsFullTime, c.car_group_name AS carGroupName FROM driver_info a LEFT JOIN car_group_driver_relation b ON a.driver_id = b.driver_id LEFT JOIN car_group_info c ON c.car_group_id = b.car_group_id WHERE 1=1
and ( a.user_id like CONCAT('%',#{name},'%') OR a.mobile like CONCAT('%',#{name},'%') OR a.driver_name like CONCAT('%',#{name},'%') )
AND b.car_group_id = #{carGroupId}
and a.driver_id =#{driverId}
and a.state=#{state}

SELECT count(1) FROM driver_info a LEFT JOIN car_group_driver_relation b ON a.driver_id = b.driver_id LEFT JOIN car_group_info c ON c.car_group_id = b.car_group_id WHERE 1=1
and ( a.user_id like CONCAT('%',#{name},'%') OR a.mobile like CONCAT('%',#{name},'%') OR a.driver_name like CONCAT('%',#{name},'%') )
AND b.car_group_id = #{carGroupId}
and a.driver_id =#{driverId}
and a.state=#{state}

select a.car_id as carId,a.car_type as carType,a.car_license as carLicense, case a.power_type when 'P001' then '汽油' when 'P002' then '柴油' when 'P003' then '电力' when 'P004' then '混合' else '无能源类型' end as powerType, a.asset_tag as assetTag,b.dept_name as deptName,c.car_group_name as carGroup, case a.source when 'S001' then '购买' when 'S002' then '租赁' when 'S003' then '借调' else '无车辆性质' end as source, case a.state when 'S000' then '启用中' when 'S001' then '禁用中' when 'S002' then '维护中' when 'S003' then '已过期' when 'S101' then '被借调' else '无状态' end as state from car_info a left join ecmp_org b on b.dept_id = a.dept_id left join car_group_info c on c.car_group_id = a.car_group_id where a.car_id in (select d.car_id from driver_car_relation_info d where d.driver_id=#{driverId})
and a.state = #{state}
and a.car_license like concat('%', #{search}, '%')

select a.driver_id as driverId,a.user_id as userId,a.driver_name as driverName,a.mobile as mobile,e.dept_name as deptName,c.car_group_name as carGroupName, a.dimission_time as dimissionTime,a.state_description as stateDescription from driver_info a left join car_group_driver_relation b on b.driver_id = a.driver_id left join car_group_info c on c.car_group_id = b.car_group_id left join ecmp_user d on d.user_id = a.user_id left join ecmp_org e on e.dept_id = d.dept_id where a.state='NV00'
and b.car_group_id = #{carGroupId}
and (a.driver_name like concat('%', #{search}, '%') or a.user_id like concat('%', #{search}, '%') or a.mobile like concat('%', #{search}, '%'))

mapper.update:
update driver_info
driver_name = #{driverName}, mobile = #{mobile}, gender = #{gender}, id_card = #{idCard}, license_photo = #{licensePhoto}, license_type = #{licenseType}, license_issue_date = #{licenseIssueDate}, license_init_issue_date = #{licenseInitIssueDate}, license_number = #{licenseNumber}, license_expire_date = #{licenseExpireDate}, user_id= #{userId}, update_by= #{optUserId}, update_time= #{updateTime},
where driver_id = #{driverId}

mapper.select:
SELECT DISTINCTROW dri.* FROM driver_info dri LEFT JOIN driver_work_info dwi ON dwi.driver_id=dri.driver_id LEFT JOIN driver_car_relation_info dcr ON dcr.driver_id=dri.driver_id LEFT JOIN car_group_driver_relation cgd ON cgd.driver_id=dri.driver_id
1=1 AND dcr.car_id IS NOT NULL AND cgd.car_group_id IS NOT NULL and dri.driver_name like CONCAT('%',#{driverNameOrPhone},'%') and dwi.caledar_date=#{workDay} and dri.driver_id=#{driverId} and dcr.car_id=#{carId} and cgd.car_group_id=#{carGroupId}
UNION
SELECT DISTINCTROW dri.* FROM driver_info dri LEFT JOIN driver_work_info dwi ON dwi.driver_id=dri.driver_id LEFT JOIN driver_car_relation_info dcr ON dcr.driver_id=dri.driver_id LEFT JOIN car_group_driver_relation cgd ON cgd.driver_id=dri.driver_id
1=1 AND dcr.car_id IS NOT NULL AND cgd.car_group_id IS NOT NULL and dri.mobile like CONCAT('%',#{driverNameOrPhone},'%') and dwi.caledar_date=#{workDay} and dri.driver_id=#{driverId} and dcr.car_id=#{carId} and cgd.car_group_id=#{carGroupId}
