mapper.select:
select
  id, config_type, ecmp_id,category_id, type, status, category, url, create_by, create_time, update_by, update_time
from ecmp.ecmp_message


        and id = #{id}


        and config_type = #{configType}


        and ecmp_id = #{ecmpId}


        and category_id = #{categoryId}


        and type = #{type}


        and status = #{status}


        and category = #{category}


        and url = #{url}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}

select id, config_type, ecmp_id,category_id, type, status,content, category, url, create_by, create_time, update_by, update_time from ecmp.ecmp_message where ecmp_id = #{ecmpId} and config_type in
#{item}
order by update_time desc

select count(*) from ecmp_message where ecmp_id = #{ecmp_id} and config_type in
#{item}
and status = '0000'
