mapper.select:
select
  id, config_type, ecmp_id,category_id, type, status, category, url, create_by, create_time, update_by, update_time,content
from ecmp.ecmp_message


        and id = #{id}


        and config_type = #{configType}


        and ecmp_id = #{ecmpId}


        and category_id = #{categoryId}


        and apply_id = #{applyId}


        and type = #{type}


        and status = #{status}


        and category = #{category}


        and content = #{content}


        and url = #{url}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}

select
id, config_type, ecmp_id,category_id, type, status, category, url, create_by, create_time, update_by, update_time,content
from ecmp_message


        and find_in_set(config_type,#{configType})


        and ecmp_id = #{ecmpId}


        and category_id = #{categoryId}


        and apply_id = #{applyId}


        and status = #{status}


        and find_id_set(category,#{category})

select id, config_type, ecmp_id,category_id,apply_id, type, status,content, category, url, create_by, create_time, update_by, update_time from ecmp.ecmp_message where ecmp_id = #{ecmpId}
and config_type in
#{item}
order by update_time desc

select count(*) from ecmp_message where ecmp_id = #{ecmp_id} and config_type in
#{item}
and status = '0000'

update.foreach:
update ecmp_message
status=#{item.status}, update_by=#{item.updateBy}, update_time=now()
where id = ${item.id}

mapper.update:
update ecmp_message

    status=#{status},
    update_by=1,
    update_time=now()

where status='0000' and category_id=#{orderId}

    and apply_id=#{applyId}
