mapper.update:
update ecmp_org
ancestors = #{ancestors}, dept_code = #{deptCode}, dept_name = #{deptName}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, status = #{status}, del_flag = #{delFlag}, update_by = #{updateBy}, update_time = #{updateTime},
where dept_id = #{deptId}

update ecmp_org
dept_code = #{deptCode}, ancestors = #{ancestors}, dept_name = #{deptName}, leader = #{leader}, phone = #{phone}, email = #{email}, update_by = #{updateBy}, update_time = #{updateTime},
where dept_id = #{deptId}

mapper.select:
SELECT dept_name FROM ecmp_org
  where del_flag='0' and dept_type = 1
  AND parent_id = #{deptId}

select count(1) from ecmp_org
where del_flag = 0
 and dept_Code = #{deptCode}
