mapper.insert:
insert into ecmp_user
dept_id, user_name, nick_name, user_type, dimission_time, pay_mode, email, phonenumber, sex, avatar, password, status, del_flag, station_code, login_ip, login_date, create_by, create_time, remark
#{deptId}, #{userName}, #{nickName}, #{userType}, #{dimissionTime}, #{payMode}, #{email}, #{phonenumber}, #{sex}, #{avatar}, #{password}, #{status}, #{delFlag}, #{stationCode}, #{loginIp}, #{loginDate}, #{createBy}, #{createTime}, #{remark}

mapper.update:
update ecmp_user
dept_id = #{deptId}, job_number = #{jobNumber}, user_name = #{userName}, nick_name = #{nickName}, user_type = #{userType}, pay_mode = #{payMode}, dimission_time = #{dimissionTime}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, del_flag = #{delFlag}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, update_time = #{updateTime} station_code = #{stationCode}
where user_id = #{userId}

mapper.insert:
insert into ecmp_user
user_name, job_number, nick_name, user_type, dimission_time, pay_mode, dept_id, station_code, email, phonenumber, sex, avatar, password, status, del_flag, login_ip, login_date, create_by, create_time, remark
#{userName}, #{jobNumber}, #{nickName}, #{userType}, #{dimissionTime}, #{payMode}, #{deptId}, #{stationCode}, #{email}, #{phonenumber}, #{sex}, #{avatar}, #{password}, #{status}, #{delFlag}, #{loginIp}, #{loginDate}, #{createBy}, #{createTime}, #{remark}

mapper.update:
update  user_regime_relation_info
set
regimen_id = #{regimenId}
where
user_id =#{userId}

mapper.select:
 select user_name as userName,phonenumber as userPhone,user_id as userId
 from ecmp_user
where user_id = #{userId}

select count(1) from ecmp_user
where del_flag = 0
 and job_number = #{jobNumber}

select * from ecmp_user
where 1=1

   and  user_id>=#{startUserId} 


    and  user_id<=#{endUserId}

select  user_id AS  userId,email,phonenumber,job_number AS  jobNumber FROM ecmp_user

     and email = #{email}
     and phonenumber = #{phonenumber}
     and user_id = #{userId}
     and job_number = #{jobNumber}
