mapper.update:
update order_info
journey_id = #{journeyId}, node_id = #{nodeId}, power_id = #{powerId}, driver_id = #{driverId}, car_id = #{carId}, user_id = #{userId}, use_car_mode = #{useCarMode}, service_type = #{serviceType}, state = #{state}, tripartite_order_id = #{tripartiteOrderId}, tripartite_platform_code = #{tripartitePlatformCode}, driver_name = #{driverName}, driver_mobile = #{driverMobile}, car_license = #{carLicense}, flight_number = #{flightNumber}, demand_car_level = #{demandCarLevel}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, order_number = #{orderNumber}, car_model =#{carModel}, car_color =#{carColor}, driver_grade =#{driverGrade}, it_is_supplement =#{itIsSupplement},
where order_id = #{orderId}

update order_info
journey_id = #{journeyId}, node_id = #{nodeId}, power_id = #{powerId}, driver_id = #{driverId}, car_id = #{carId}, user_id = #{userId}, use_car_mode = #{useCarMode}, service_type = #{serviceType}, state = #{state}, tripartite_order_id = #{tripartiteOrderId}, tripartite_platform_code = #{tripartitePlatformCode}, driver_name = #{driverName}, driver_mobile = #{driverMobile}, car_license = #{carLicense}, flight_number = #{flightNumber}, demand_car_level = #{demandCarLevel}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, order_number = #{orderNumber}, car_model =#{carModel}, car_color =#{carColor}, driver_grade =#{driverGrade},
where order_id = #{orderId}

mapper.select:
select a.order_id AS orderId, a.journey_id journeyId, a.state, a.service_type serviceType, a.user_id userId, a.driver_id driverId, a.use_car_mode useCarMode, a.driver_mobile driverPhone, e.address startAddress, DATE_FORMAT(e.action_time, '%m月%d日 %H:%i ') useCarTime, e.action_time useCarDate, e.longitude startLongitude, e.latitude startLatitude, d.address endAddress, d.longitude endLongitude , d.latitude endLatitude, g.charter_car_type charterCarType,
g.use_time useTime, g.start_date startDate, g.end_date endDate,
(select state from order_state_trace_info sti where sti.order_id=#{orderId} order by trace_id desc limit 1) labelState from order_info a left join journey_info g on g.journey_id=a.journey_id left join order_address_info e on e.order_id=a.order_id and e.type='A000' left join order_address_info d on d.order_id=a.order_id and d.type='A999' where a.order_id=#{orderId}

SELECT messageId,messageType,messageTypeStr,count(*) messageCount from ( select order_id as messageId
,4 as messageType,'派车'as messageTypeStr
,1 as messageType,'新任务'as messageTypeStr
from order_info where find_in_set(state,#{states})
and driver_id=#{driveId}
) a group by a.messageType

SELECT messageId,messageType,'取消任务' messageTypeStr,count(*) messageCount from (
   select order_id as messageId,4 as messageType from order_info
   where state=#{state} and driver_id=#{driverId}
   ) a group by a.messageType

SELECT a.order_id AS orderId, b.nick_name AS applyUserName, b.user_name AS applyUserMobile, a.journey_id AS journeyId, a.node_id AS nodeId, a.update_time AS optDate, a.state AS state, a.service_type as serviceType, h.it_is_return as itIsReturn, h.wait_time_long as waitTimeLong, f.SHORT_NAME as cityName FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN ecmp_user b ON a.user_id = b.user_id LEFT JOIN order_address_info c ON a.order_id = c.order_id LEFT JOIN journey_info h on a.journey_id=h.journey_id LEFT JOIN china_city f ON c.city_postal_code =f.CITY_CODE WHERE d.state NOT IN ('S270', 'S277', 'S279') AND a.state in ('S100','S199','S299','S500','S600','S616','S699','S900') and (a.use_car_mode is null or a.use_car_mode='W100' or a.use_car_mode='' or a.use_car_mode='W200') and a.company_id=#{companyId}
and b.user_name LIKE concat('%',#{applyMobile},'%')
AND b.nick_name LIKE concat('%',#{applyName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id

select count(1) from (SELECT count(1) FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN ecmp_user b ON a.user_id = b.user_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state NOT IN ('S270', 'S277', 'S279') AND a.state in ('S100','S199','S299','S500','S600','S616','S699','S900') and (a.use_car_mode is null or a.use_car_mode='W100' or a.use_car_mode='')
and b.user_name LIKE concat('%',#{applyMobile},'%')
AND b.nick_name LIKE concat('%',#{applyName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id ) m

SELECT a.order_id AS orderId, b.driver_name AS applyUserName, b.mobile AS applyUserMobile, a.journey_id AS journeyId, a.node_id AS nodeId, a.update_time AS optDate, a.state AS state, h.it_is_return as itIsReturn, h.wait_time_long as waitTimeLong, f.SHORT_NAME as cityName FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN driver_info b ON a.driver_id = b.driver_id LEFT JOIN order_address_info c ON a.order_id = c.order_id LEFT JOIN journey_info h on a.journey_id=h.journey_id LEFT JOIN china_city f ON c.city_postal_code =f.CITY_CODE WHERE d.state IN ('S270', 'S277', 'S279') and a.state in ('S299','S500','S600','S616','S900','S699') and a.use_car_mode is not null
and b.mobile LIKE concat('%',#{driverMobile},'%')
AND b.driver_name LIKE concat('%',#{driverName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id

select count(1) from (SELECT count(1) FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN driver_info b ON a.driver_id = b.driver_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state IN ('S270', 'S277', 'S279') and a.state in ('S299','S500','S600','S616','S900','S699') and a.use_car_mode is not null
and b.mobile LIKE concat('%',#{driverMobile},'%')
AND b.driver_name LIKE concat('%',#{driverName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id ) m

SELECT
   ord.*
FROM  order_info ord
LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id

     and  ord.car_id=#{carId}  
     and jpp.planned_arrival_time > #{setOutTime}  
     and ord.driver_id = #{driverId}  

ORDER BY  ord.order_id DESC

SELECT ord.* FROM order_info ord LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id
and ord.car_id = #{carId} and #{arrivalTime} > jpp.planned_departure_time and ord.driver_id = #{driverId}
ORDER BY ord.order_id DESC

SELECT
    ord.order_id,
    jpp.planned_departure_time as create_time
FROM  order_info ord
LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id

     and  ord.car_id=#{carId}  
     and jpp.planned_arrival_time >#{setOutTime}  
     and ord.driver_id = #{driverId}

ORDER BY  ord.order_id DESC

SELECT ord.order_id, jpp.planned_arrival_time as update_time FROM order_info ord LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id
and ord.car_id=#{carId} and #{arrivalTime} > jpp.planned_departure_time and ord.driver_id = #{driverId}
ORDER BY ord.order_id DESC

select order_id orderId,state,use_car_mode useCarMode,
(select state from order_state_trace_info sti where sti.order_id=#{orderId} order by trace_id desc limit 1) labelState
from  order_info where order_id = #{orderId}

SELECT count(*) FROM order_info where state='S100'
 and user_id in

   #{userId}

SELECT count(*) FROM order_info where user_id in
#{userId}
AND order_id IN (SELECT order_id from ( SELECT order_id ,state FROM ( SELECT order_id, state FROM order_state_trace_info ORDER BY trace_id DESC) r GROUP BY r.order_id having r.state = 'S279' ) tmp)

select scene_id from scene_regime_relation where regimen_id=(
    SELECT regimen_id FROM `journey_info` where journey_id=#{journeyId}
)

SELECT DISTINCT -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 操作权限 0：有权限 1:没有权限 '0' operationPermission, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 eu.nick_name useCarUserName, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 oaitwo.address endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, round((UNIX_TIMESTAMP(now())-UNIX_TIMESTAMP(oi.create_time))/60) waitingTime, -- 服务类型 oi.service_type serverType, -- 状态 case when right (z.state,3)='100' then '待派车' when right (z.state,3) >= '299' and right (z.state,3) <= '900' then '已处理' when right (z.state,3)='921' then '已过期' when right (z.state,3)='930' then '已驳回' when right (z.state,3)='911' then '已取消' ELSE '' END state, -- 操作时间 case when right (oi.state,3)='100' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S100' limit 1) when right (oi.state,3)='299' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state ='S299' limit 1) when right (oi.state,3)= '900' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state in ('S921','S930') limit 1) ELSE now() END opDate FROM order_info oi LEFT JOIN car_group_info cgi ON oi.company_id = cgi.company_id LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON (oai.order_id = oi.order_id and oai.type='A000') LEFT JOIN order_address_info oaitwo ON (oaitwo.order_id = oi.order_id and oaitwo.type='A999') LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code LEFT JOIN (select order_id,state from order_state_trace_info where trace_id in (select Max(trace_id) traceId from order_state_trace_info group by order_id)) z on z.order_id=oi.order_id WHERE 1 = 1 and oi.order_id in (select distinct order_id from order_state_trace_info where order_id in (select distinct order_id from order_state_trace_info where state in ('S299', 'S921', 'S100')) and order_id not in (select distinct order_id from order_state_trace_info where state in ('S270', 'S279', 'S277')) )
and oi.company_id=#{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and cc.SHORT_NAME=#{cityCode}
and oi.use_car_mode=#{useCarMode}
and oi.state=#{dispatchStatus}
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and oi.it_is_supplement='N000' and oi.user_id in (select distinct user_id from ecmp_user where dept_id in (select dept_id from car_group_serve_org_relation where car_group_id in (select car_group_id from car_group_dispatcher_info where user_id=#{userId}))) ORDER BY oi.state,oai.action_time ASC

SELECT DISTINCT -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 操作权限 0：有权限 1:没有权限 '1' operationPermission, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 eu.nick_name useCarUserName, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 oaitwo.address endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, round((UNIX_TIMESTAMP(now())-UNIX_TIMESTAMP(oi.create_time))/60) waitingTime, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, -- 服务类型 oi.service_type serverType, -- 状态 case when right (z.state,3)='100' then '待派车' when right (z.state,3) >= '299' and right (z.state,3) <= '900' then '已处理' when right (z.state,3)='921' then '已过期' when right (z.state,3)='930' then '已驳回' when right (z.state,3)='911' then '已取消' ELSE '' END state, -- 操作时间 case when right (oi.state,3)='100' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S100' limit 1) when right (oi.state,3)='299' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state ='S299' limit 1) when right (oi.state,3)= '900' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state in ('S921','S930') limit 1) ELSE now() END opDate FROM order_info oi LEFT JOIN car_group_info cgi ON oi.company_id = cgi.company_id LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON (oai.order_id = oi.order_id and oai.type='A000') LEFT JOIN order_address_info oaitwo ON (oaitwo.order_id = oi.order_id and oaitwo.type='A999') LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code LEFT JOIN (select order_id,state from order_state_trace_info where trace_id in (select Max(trace_id) traceId from order_state_trace_info group by order_id)) z on z.order_id=oi.order_id WHERE 1 = 1 and oi.order_id in (select distinct order_id from order_state_trace_info where order_id in (select distinct order_id from order_state_trace_info where state in ('S299', 'S921', 'S100')) and order_id not in (select distinct order_id from order_state_trace_info where state in ('S270', 'S279', 'S277')) )
and oi.company_id=#{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and cc.SHORT_NAME=#{cityCode}
and oi.use_car_mode=#{useCarMode}
and oi.state=#{dispatchStatus}
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and oi.it_is_supplement='N000'
and oi.order_id not in
#{orderId}
ORDER BY oi.state,oai.action_time ASC

select * from ( SELECT DISTINCT -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 操作权限 0：有权限 1:没有权限 '1' operationPermission, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 jpi.name useCarUserName, -- 乘车人电话 jpi.mobile userCarUserMobile, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 oaitwo.address endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, CASE WHEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) >= 0 THEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) ELSE 0 END AS waitingTime, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, -- 服务类型 ji.charter_car_type serverType, GROUP_CONCAT(DISTINCT jai.address_info) addressInfo, -- 车型名字 ecti.name carTypeName, -- 状态 case when right (z.state,3)='100' then '待派车' when right (z.state,3) >= '299' and right (z.state,3) <= '900' then '已处理' when right (z.state,3)='921' then '已过期' when right (z.state,3)='930' then '已驳回' ELSE '' END state, -- 操作时间 case when right (oi.state,3)='100' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S100' limit 1) when right (oi.state,3)='299' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state ='S299' limit 1) when right (oi.state,3)= '900' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state in ('S921','S930') limit 1) ELSE now() END opDate, z.state orderByState FROM order_info oi LEFT JOIN car_group_info cgi ON oi.company_id = cgi.company_id LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON (oai.order_id = oi.order_id and oai.type='A000') LEFT JOIN order_address_info oaitwo ON (oaitwo.order_id = oi.order_id and oaitwo.type='A999') LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code LEFT JOIN (select order_id,state from order_state_trace_info where trace_id in (select Max(trace_id) traceId from order_state_trace_info group by order_id)) z on z.order_id=oi.order_id LEFT JOIN journey_address_info jai ON jai.journey_id = ji.journey_id left join apply_info ai on ai.journey_id=oi.journey_id left join enterprise_car_type_info ecti on ecti.car_type_id=ai.car_type_id WHERE 1 = 1 and oi.order_id in (select distinct order_id from order_state_trace_info where order_id in (select distinct order_id from order_state_trace_info where state in ('S299', 'S921', 'S100')) and order_id not in (select distinct order_id from order_state_trace_info where state in ('S270', 'S279', 'S277','S911')) )
and oi.company_id=#{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and oi.use_car_mode=#{useCarMode}
and oai.city_postal_code=#{cityCode}
and oi.it_is_supplement='N000'
and oi.order_id not in
#{orderId}
GROUP BY oi.journey_id ) t where 1=1
and DATE_FORMAT(t.opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(t.opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and t.state=#{dispatchStatus}
order by state='待派车' DESC ,state='已派车' DESC , useCarDate ASC

SELECT DISTINCT -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 eu.nick_name useCarUserName, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, round((UNIX_TIMESTAMP(now())-UNIX_TIMESTAMP(oi.create_time))/60) waitingTime, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 -- oaitwo.address endSite, if (GROUP_CONCAT(jai.address_info) is null,oaitwo.address,GROUP_CONCAT(jai.address_info,',' ,oaitwo.address)) endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, -- 服务类型 oi.service_type serverType, -- 状态 oi.state state, -- 操作时间 oi.update_time opDate, -- 申请原因 ai.reason reason, -- 成本中心 eor.dept_name costCenter, -- 外部车队名称 ai.it_is_self_driver itIsSelfDriver, -- 是否自驾 cgiTwo.car_group_name carGroupName, -- GROUP_CONCAT(jai.address_info) addressInfo, -- 项目编号 pi.name projectName FROM order_info oi LEFT JOIN car_group_info cgi ON oi.company_id = cgi.company_id LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN apply_info ai on ai.journey_id=oi.journey_id LEFT JOIN ecmp_org eori on ai.cost_center=eori.dept_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON oai.order_id = oi.order_id and oai.type='A000' LEFT JOIN order_address_info oaitwo ON oaitwo.order_id = oi.order_id and oaitwo.type='A999' LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code LEFT JOIN project_info pi ON pi.project_id = ai.project_id LEFT JOIN order_state_trace_info osti on oi.order_id = osti.order_id LEFT JOIN car_group_info cgiTwo on cgiTwo.car_group_id = ai.outer_car_group_id LEFT JOIN journey_address_info jai ON jai.journey_id = ji.journey_id WHERE 1 = 1 and oi.company_id=#{companyId} and osti.create_by=#{userId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and cc.SHORT_NAME=#{cityCode}
and oi.use_car_mode=#{useCarMode}
and oi.state=#{dispatchStatus}
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and oi.it_is_supplement = 'D000' GROUP BY oi.journey_id ORDER BY oi.state,oai.action_time ASC

SELECT DISTINCT -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 操作权限 0：有权限 1:没有权限 '0' operationPermission, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 eu.nick_name useCarUserName, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, round((UNIX_TIMESTAMP(now())-UNIX_TIMESTAMP(oi.create_time))/60) waitingTime, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 oaitwo.address endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, -- 服务类型 oi.service_type serverType, -- 状态 case when right (z.state,3)='270' then '待改派' when right (z.state,3)='279' then '已通过' when right (z.state,3)='277' then '以驳回' when right (z.state,3)='921' then '已过期' ELSE '' END state, -- 操作时间 case when right (z.state,3)='270' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S270' limit 1) when right (z.state,3) IN ('279','277') then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state in ('S277','S279') limit 1) when right (z.state,3)= '921' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S921' limit 1) ELSE now() END opDate FROM order_info oi LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN (select order_id,state from order_state_trace_info where trace_id in (select Max(trace_id) traceId from order_state_trace_info group by order_id)) z on z.order_id=oi.order_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON (oai.order_id = oi.order_id and oai.type='A000') LEFT JOIN order_address_info oaitwo ON (oaitwo.order_id = oi.order_id and oaitwo.type='A999') LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code WHERE 1 = 1 and oi.order_id in (select distinct order_id from order_state_trace_info where state in ('S279', 'S277') and create_by=#{userId} union (SELECT distinct order_id from order_state_trace_info where order_id in (select distinct order_id from order_state_trace_info where state='S299' and create_by=#{userId}) and state='S270') union (SELECT distinct order_id from order_state_trace_info where state='S921') )
and oi.company_id=#{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and cc.SHORT_NAME=#{cityCode}
and oi.use_car_mode=#{useCarMode}
and oi.state=#{dispatchStatus}
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and oi.it_is_supplement='N000' ORDER BY oi.state,oai.action_time ASC

