select * from ( SELECT DISTINCT od.next_car_group_id nextCarGroupId, -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 操作权限 0：有权限 1:没有权限 '0' operationPermission, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 jpi.name useCarUserName, -- 乘车人电话 jpi.mobile userCarUserMobile, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 oaitwo.address endSite, -- if (GROUP_CONCAT(jai.address_info) is null,oaitwo.address,GROUP_CONCAT(jai.address_info,',' ,oaitwo.address)) endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, CASE WHEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) >= 0 THEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) ELSE 0 END AS waitingTime, -- 服务类型 ji.charter_car_type serverType, -- 状态 case when right (osti.state,3)='100' then '待派车' when right (osti.state,3) >= '299' and right (osti.state,3) <= '900' then '已处理' when right (osti.state,3)='921' then '已过期' when right (osti.state,3)='930' then '已驳回' ELSE '' END state, -- 操作时间 case when right (oi.state,3)='100' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S100' limit 1) when right (oi.state,3)='299' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S299' limit 1) when right (oi.state,3)= '900' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S921' limit 1) ELSE now() END opDate, -- 包车的总天数 ji.use_time charterCarDaysCount, -- 车型id ai.car_type_id carTypeId, -- 车型名字 ecti.name carTypeName, -- 用车备注 ai.notes, od.car_group_user_mode carGroupUserMode, od.it_is_self_driver selfDriver, oai.city_postal_code useCarCityCode, od.it_is_use_inner_car_group useCarGroupType, ai.it_is_self_driver itIsSelfDriver, cgi.car_group_name carGroupName, GROUP_CONCAT( DISTINCT jai.address_info) addressInfo, ai.reason reason, ai.safe_remind safeRemind, osti.state orderByState FROM order_info oi left join (SELECT state,order_id FROM `order_state_trace_info` where trace_id in (select max(trace_id) from order_state_trace_info GROUP BY order_id) ) osti on osti.order_id=oi.order_id LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON (oai.order_id = oi.order_id and oai.type='A000') LEFT JOIN order_address_info oaitwo ON (oaitwo.order_id = oi.order_id and oaitwo.type='A999') LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code LEFT JOIN order_dispatche_detail_info od ON od.order_id = oi.order_id left join journey_user_car_power jucp on jucp.node_id=oi.node_id left join apply_info ai on ai.apply_id=jucp.apply_id left join enterprise_car_type_info ecti on ecti.car_type_id=ai.car_type_id LEFT JOIN car_group_info cgi ON cgi.car_group_id = ai.outer_car_group_id LEFT JOIN journey_address_info jai ON jai.journey_id = ji.journey_id WHERE 1 = 1
and oi.state = #{homePageWaitingCarState}
and oi.state = #{homePageExpireCarState}
and right ( oi.state,3) >= '299' and right (oi.state,3) <= '900'
and oi.create_time >= #{homeDynamicBeginTime}
and oi.create_time <= #{homeDynamicEndTime}
and oi.order_id in (select distinct order_id from order_state_trace_info where order_id in (select distinct order_id from order_state_trace_info where state in ('S299', 'S921', 'S100','S930')) and order_id not in (select distinct order_id from order_state_trace_info where state in ('S270', 'S279', 'S277','S911')) )
and oi.company_id=#{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and oi.use_car_mode=#{useCarMode}
and oi.order_id=#{orderId}
and oai.city_postal_code=#{cityCode}
and oi.it_is_supplement='N000' and (oi.user_id in (select distinct user_id from ecmp_user where dept_id in (select dept_id from car_group_serve_org_relation where car_group_id in (select car_group_id from car_group_dispatcher_info where user_id=#{userId}))) ) and oai.city_postal_code in( SELECT city FROM car_group_serve_scope_info WHERE car_group_id IN ( SELECT car_group_id FROM car_group_dispatcher_info WHERE user_id = #{userId} ) ) GROUP BY oi.journey_id ) t where 1=1
and DATE_FORMAT(t.opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(t.opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and t.state=#{dispatchStatus}
order by state='待派车' DESC ,state='已派车' DESC , useCarDate ASC

