and oi.order_id=#{orderId}
and oi.it_is_supplement='N000' and (oi.user_id in (select distinct user_id from ecmp_user where dept_id in (select dept_id from car_group_serve_org_relation where car_group_id in (select car_group_id from car_group_dispatcher_info where user_id=#{userId}))) ) and oai.city_postal_code in( SELECT city FROM car_group_serve_scope_info WHERE car_group_id IN ( SELECT car_group_id FROM car_group_dispatcher_info WHERE user_id = #{userId} ) ) ORDER BY oi.state,oai.action_time ASC

SELECT DISTINCT od.next_car_group_id nextCarGroupId, -- 订单id oi.order_id orderId, -- 部门名称 eo.dept_name AS deptName, -- 公司名称 eor.dept_name AS companyName, -- 操作权限 0：有权限 1:没有权限 '0' operationPermission, -- 申请人姓名 eu.nick_name applyUserName, -- 申请人手机号 eu.phonenumber applyUserTel, -- 乘车人 jpi.name useCarUserName, -- 乘车人电话 jpi.mobile userCarUserMobile, -- 同行人数量 jpi.peer_number travelPartnerCount, -- 开始时间 oai.action_time useCarDate, -- 预计结束时间 oaitwo.action_time endDate, -- 上车地址 oai.address startSite, -- 下车地址 oaitwo.address endSite, -- 是否往返 ji.it_is_return itIsReturn, -- 预计等待时长 ji.wait_time_long waitMinute, -- 用车城市 cc.SHORT_NAME useCarCity, -- 用车场景 si.`name` userCarScene, -- 用车制度 ri.`name` userCarRegime, CASE WHEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) >= 0 THEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) ELSE 0 END AS waitingTime, -- 服务类型 ji.charter_car_type serverType, -- 状态 case when right (osti.state,3)='100' then '待派车' when right (osti.state,3) >= '299' and right (osti.state,3) <= '901' then '已处理' when right (osti.state,3)='921' then '已过期' when right (osti.state,3)='930' then '已驳回' ELSE '' END state, -- 操作时间 case when right (oi.state,3)='100' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S100' limit 1) when right (oi.state,3)='299' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S299' limit 1) when right (oi.state,3)= '900' then (select create_time from order_state_trace_info sti where sti.order_id=oi.order_id and sti.state='S921' limit 1) ELSE now() END opDate, -- 包车的总天数 ji.use_time charterCarDaysCount, -- 车型id ai.car_type_id carTypeId, -- 车型名字 ecti.name carTypeName, -- 用车备注 ai.notes, od.car_group_user_mode carGroupUserMode, od.it_is_self_driver selfDriver, oai.city_postal_code useCarCityCode, od.it_is_use_inner_car_group useCarGroupType, ai.reason reason FROM order_info oi left join (SELECT state,order_id FROM `order_state_trace_info` where trace_id in (select max(trace_id) from order_state_trace_info GROUP BY order_id) ) osti on osti.order_id=oi.order_id LEFT JOIN ecmp_user eu ON oi.user_id = eu.user_id LEFT JOIN ecmp_org eo ON eu.dept_id = eo.dept_id LEFT JOIN ecmp_org eor ON eo.company_id= eor.dept_id LEFT JOIN journey_info ji ON ji.journey_id = oi.journey_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = oi.journey_id LEFT JOIN regime_info ri ON ri.regimen_id = ji.regimen_id LEFT JOIN scene_regime_relation srr ON srr.regimen_id = ri.regimen_id LEFT JOIN scene_info si ON srr.scene_id = si.scene_id LEFT JOIN order_address_info oai ON (oai.order_id = oi.order_id and oai.type='A000') LEFT JOIN order_address_info oaitwo ON (oaitwo.order_id = oi.order_id and oaitwo.type='A999') LEFT JOIN china_city cc ON cc.CITY_CODE = oai.city_postal_code LEFT JOIN order_dispatche_detail_info od ON od.order_id = oi.order_id -- LEFT JOIN car_group_info cgi on (cgi.car_group_id = od.car_cg_id or cgi.car_group_id=od.driver_cg_id or od.next_car_group_id = cgi.car_group_id) left join journey_user_car_power jucp on jucp.node_id=oi.node_id left join apply_info ai on ai.apply_id=jucp.apply_id left join enterprise_car_type_info ecti on ecti.car_type_id=ai.car_type_id WHERE 1 = 1
and oi.order_id in ( select a.orderId from (select order_id orderId,max(state) mstate from order_state_trace_info GROUP BY order_id ) a where a.mstate = 'S100' )
and right (osti.state,3)='100'
and right (osti.state,3)='921'
and right (osti.state,3) >= '299' and right (osti.state,3) <= '901'
and oi.create_time >= #{homeDynamicBeginTime}
and oi.create_time <= #{homeDynamicEndTime}
and oi.company_id=#{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyMobile},'%')
and cc.SHORT_NAME=#{cityCode}
and oi.use_car_mode=#{useCarMode}
and oi.state=#{dispatchStatus}
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{optStartDate},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(opDate,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{optEndDate},'%Y-%m-%d %H:%i:%S')
and oi.order_id=#{orderId}
and oi.it_is_supplement='N000' and (oi.user_id in (select distinct user_id from ecmp_user where dept_id in (select dept_id from car_group_serve_org_relation where car_group_id in (select car_group_id from car_group_dispatcher_info where user_id=#{userId}))) ) and oai.city_postal_code in( SELECT city FROM car_group_serve_scope_info WHERE car_group_id IN ( SELECT car_group_id FROM car_group_dispatcher_info WHERE user_id = #{userId} ) ) ORDER BY oi.state,oai.action_time ASC

SELECT DISTINCT -- 申请id ai.apply_id applyId, -- 申请人姓名 eu.nick_name applyName, -- 申请人手机号 eu.phonenumber applyPhoneNumber, -- 用车人姓名 jpi.name vehicleUserName, -- 用车人手机号 jpi.mobile vehicleUserPhoneNumber, -- 同行人数 jpi.peer_number peerNumber, -- 用车单位 eo.dept_name vehicleUserCompany, -- 服务类型 case when right (ji.charter_car_type,3)='001' then '半日租(4小时)' when right (ji.charter_car_type,3)='002' then '整日租(8小时)' when right (ji.charter_car_type,3)='009' then CONCAT('多日租','(',ji.use_time,'日',')') ELSE '' END charterCarType, -- 服务车型 ecty.name carLevel, -- 车辆类型 ci.car_type carType, -- 车辆车牌号 ci.car_license carLicense, -- 车辆颜色 ci.car_color carColor, -- 用车时间 ji.use_time useTime, -- 用车开始时间 ji.start_date beginTime, -- 用车结束时间 ji.end_date endTime, -- 申请事由 ai.reason vehicleUseReason, -- 用车备注 ai.notes vehicleRemarks,
DATE_FORMAT(ai.create_time, '%Y-%m-%d %H:%i') createTime, -- 车辆归属 cgi.car_group_name carCompany, -- 驾驶员姓名 di.driver_name driverName, -- 等待时长 CASE WHEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) >= 0 THEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) ELSE 0 END AS waitingTime, -- 驾驶员手机号 di.mobile driverPhone, -- 驾驶员归属 cgiTwo.car_group_name driverCompany, -- 外部车队名称 cgiThree.car_group_name outerCarGroupName, -- 是否自驾 ai.it_is_self_driver itIsSelfDriver, -- 状态 oai1.address beginAddress, -- 出发地址 oai2.address endAddress, -- if (GROUP_CONCAT(jai.address_info) is null,oai2.address,GROUP_CONCAT(jai.address_info,',' ,oai2.address)) endAddress, -- 到达地址 oi.order_number orderNumber, -- 订单编号 oi.use_car_mode useCarMode, -- 用车方式 oscdrio.amount amount, oi.order_id orderId, GROUP_CONCAT( DISTINCT jai.address_info) addressInfo, if(oddi.it_is_use_inner_car_group is null,0,1) canUpdateDetail, -- 费用合计 case when z.state='S100' then '待派车' when right (z.state,3) >= '299' and right (z.state,3) <= '901' then '已派车' when ai.state='S004' OR z.state='S911' then '已撤销' when ai.state='S005' OR z.state='S921' then '已过期' when z.state='S930' then '已驳回' ELSE '' END orderState FROM apply_info ai LEFT JOIN journey_info ji ON ai.journey_id = ji.journey_id LEFT JOIN ecmp_user eu ON eu.user_id = ji.user_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = ji.journey_id and jpi.it_is_peer = '00' LEFT JOIN ecmp_org eo ON eo.dept_id = eu.dept_id LEFT JOIN order_info oi ON oi.journey_id = ji.journey_id LEFT JOIN order_dispatche_detail_info oddi ON oi.order_id = oddi.order_id LEFT JOIN car_info ci ON ci.car_id = oi.car_id LEFT JOIN enterprise_car_type_info ecty ON ecty.car_type_id = ai.car_type_id LEFT JOIN car_group_info cgi ON cgi.car_group_id = ci.car_group_id LEFT JOIN driver_info di ON di.driver_id = oi.driver_id LEFT JOIN car_group_driver_relation cgdr ON cgdr.driver_id = di.driver_id LEFT JOIN car_group_info cgiTwo ON cgiTwo.car_group_id = cgdr.car_group_id LEFT JOIN (select order_id,state from order_state_trace_info where trace_id in (select Max(trace_id) traceId from order_state_trace_info group by order_id)) z on z.order_id=oi.order_id left join (select order_id,type,address,address_long from order_address_info where type = 'A000') oai1 on oai1.order_id = oi.order_id left join (select order_id,type,address,address_long from order_address_info where type = 'A999') oai2 on oai2.order_id = oi.order_id left join order_settling_info osi on osi.order_id = oi.order_id left join (select sum(total_fee) amount,order_id orderId from order_service_cost_detail_record_info group by order_id) oscdrio on oscdrio.orderId = oi.order_id LEFT JOIN car_group_info cgiThree ON cgiThree.car_group_id = ai.outer_car_group_id LEFT JOIN journey_address_info jai ON jai.journey_id = ji.journey_id where oi.service_type='5000'
and eu.user_id = #{userId}
and eu.dept_id = #{deptId}
and ai.apply_id = #{applyId}
and ai.company_id = #{companyId}
and eu.nick_name like concat('%',#{applyName},'%')
and eu.phonenumber like concat('%',#{applyPhoneNumber},'%')
and jpi.name like concat('%',#{vehicleUserName},'%')
and jpi.mobile like concat('%',#{vehicleUserPhoneNumber},'%')
and eo.dept_name=#{vehicleUserCompany}
and ji.charter_car_type=#{charterCarType}
and jpi.peer_number=#{peerNumber}
and ecty.name=#{carLevel}
and DATE_FORMAT(ji.start_date,'%Y-%m-%d %H:%i') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d %H:%i')
and DATE_FORMAT(ji.end_date,'%Y-%m-%d %H:%i') <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i')
and z.state='S100'
and (right (z.state,3) BETWEEN '299' and '901')
and ai.state='S005' OR z.state='S921'
or ( eu.dept_id = #{deptId} and oi.service_type='5000' and z.state='S930' and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(ai.create_time))
and oi.state = #{homePageToBeConfirmedState}
and ai.create_time >= #{homeDynamicBeginTime}
and ai.create_time <= #{homeDynamicEndTime}
and z.state='S100'
and (right (z.state,3) BETWEEN '299' and '901')
and (ai.state='S004' OR z.state='S911')
and (ai.state='S005' OR z.state='S921')
and z.state='S930'
and oi.order_number= like concat('%', #{orderNumber}, '%')
GROUP BY ai.journey_id order by orderState='待派车' DESC ,orderState='已派车' DESC , beginTime ASC

SELECT DISTINCT -- 申请id ai.apply_id applyId, -- 申请人姓名 eu.nick_name applyName, -- 申请人手机号 eu.phonenumber applyPhoneNumber, -- 用车人姓名 jpi.name vehicleUserName, -- 用车人手机号 jpi.mobile vehicleUserPhoneNumber, -- 同行人数 jpi.peer_number peerNumber, -- 用车单位 eo.dept_name vehicleUserCompany, -- 服务类型 case when right (ji.charter_car_type,3)='001' then '半日租(4小时)' when right (ji.charter_car_type,3)='002' then '整日租(8小时)' when right (ji.charter_car_type,3)='009' then CONCAT('多日租','(',ji.use_time,'日',')') ELSE '' END charterCarType, -- 服务车型 ecty.name carLevel, -- 车辆类型 ci.car_type carType, -- 车辆车牌号 ci.car_license carLicense, -- 车辆颜色 ci.car_color carColor, -- 用车时间 ji.use_time useTime, -- 用车开始时间 ji.start_date beginTime, -- 用车结束时间 ji.end_date endTime, -- 申请事由 ai.reason vehicleUseReason, -- 用车备注 ai.notes vehicleRemarks,
DATE_FORMAT(ai.create_time, '%Y-%m-%d %H:%i') createTime, -- 车辆归属 cgi.car_group_name carCompany, -- 驾驶员姓名 di.driver_name driverName, -- 等待时长 CASE WHEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) >= 0 THEN round(( UNIX_TIMESTAMP( ji.start_date) - UNIX_TIMESTAMP( NOW() )) / 60 ) ELSE 0 END AS waitingTime, -- 驾驶员手机号 di.mobile driverPhone, -- 驾驶员归属 cgiTwo.car_group_name driverCompany, -- 外部车队名称 cgiThree.car_group_name outerCarGroupName, -- 是否自驾 ai.it_is_self_driver itIsSelfDriver, -- 状态 oai1.address beginAddress, -- 出发地址 oai2.address endAddress, -- if (GROUP_CONCAT(jai.address_info) is null,oai2.address,GROUP_CONCAT(jai.address_info,',' ,oai2.address)) endAddress, -- 到达地址 oi.order_number orderNumber, -- 订单编号 oi.use_car_mode useCarMode, -- 用车方式 oscdrio.amount amount, oi.order_id orderId, GROUP_CONCAT( DISTINCT jai.address_info) addressInfo, -- 费用合计 case when z.state='S100' then '待派车' when right (z.state,3) >= '299' and right (z.state,3) <= '901' then '已派车' when ai.state='S004' OR z.state='S911' then '已撤销' when ai.state='S005' OR z.state='S921' then '已过期' when z.state='S930' then '已驳回' ELSE '' END orderState FROM apply_info ai LEFT JOIN journey_info ji ON ai.journey_id = ji.journey_id LEFT JOIN ecmp_user eu ON eu.user_id = ji.user_id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = ji.journey_id and jpi.it_is_peer = '00' LEFT JOIN ecmp_org eo ON eo.dept_id = eu.dept_id LEFT JOIN order_info oi ON oi.journey_id = ji.journey_id LEFT JOIN car_info ci ON ci.car_id = oi.car_id LEFT JOIN enterprise_car_type_info ecty ON ecty.car_type_id = ai.car_type_id LEFT JOIN car_group_info cgi ON cgi.car_group_id = ci.car_group_id LEFT JOIN driver_info di ON di.driver_id = oi.driver_id LEFT JOIN car_group_driver_relation cgdr ON cgdr.driver_id = di.driver_id LEFT JOIN car_group_info cgiTwo ON cgiTwo.car_group_id = cgdr.car_group_id LEFT JOIN (select order_id,state from order_state_trace_info where trace_id in (select Max(trace_id) traceId from order_state_trace_info group by order_id)) z on z.order_id=oi.order_id left join (select order_id,type,address,address_long from order_address_info where type = 'A000') oai1 on oai1.order_id = oi.order_id left join (select order_id,type,address,address_long from order_address_info where type = 'A999') oai2 on oai2.order_id = oi.order_id left join order_settling_info osi on osi.order_id = oi.order_id left join (select sum(total_fee) amount,order_id orderId from order_service_cost_detail_record_info group by order_id) oscdrio on oscdrio.orderId = oi.order_id LEFT JOIN car_group_info cgiThree ON cgiThree.car_group_id = ai.outer_car_group_id LEFT JOIN journey_address_info jai ON jai.journey_id = ji.journey_id where oi.service_type='5000'
and eu.user_id = #{userId}
and eu.dept_id = #{deptId}
and ai.company_id = #{companyId}
and eu.nick_name = #{applyName}
and eu.phonenumber = #{applyPhoneNumber}
and jpi.name=#{vehicleUserName}
and jpi.mobile=#{vehicleUserPhoneNumber}
and eo.dept_name=#{vehicleUserCompany}
and ji.charter_car_type=#{charterCarType}
and jpi.peer_number=#{peerNumber}
and ecty.name=#{carLevel}
and DATE_FORMAT(ji.start_date,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(ji.end_date,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%S')
and right (z.state,3)='100'
and (right (z.state,3) BETWEEN '299' and '901')
and (ai.state='S005' OR z.state='S921')
or right (z.state,3)='930'
and right (z.state,3)='699'
and ai.create_time >= #{homeDynamicBeginTime}
and ai.create_time <= #{homeDynamicEndTime}
GROUP BY ai.journey_id order by orderState='待派车' DESC ,orderState='已派车' DESC , beginTime ASC

select count(oi.order_id) as toBeServed, count(oia.order_id) as waitingToLeave, count(oib.order_id) as toBePickedUp, (select count(oic.order_id) from order_info oic where oic.state ='S500') as takingOver, (select count(oid.order_id) from order_info oid where oid.state ='S600') as etcUpperCar, (select count(oie.order_id) from order_info oie where oie.state ='S616') as inService, (select count(oif.order_id) from order_info oif where oif.state ='S635') as serviceSuspension, (select count(oig.order_id) from order_info oig where oig.state ='S699') as toBeConfirmed, (select count(oih.order_id) from order_info oih where oih.state ='S900') as completed, (select count(oii.order_id) from order_info oii where oii.state ='S911') as cancelled from order_info oi left join order_dispatche_detail_info oia on oia.order_id = oi.order_id and oia.it_is_self_driver ='Y000' and oi.state='S299' left join order_dispatche_detail_info oib on oib.order_id = oi.order_id and oib.it_is_self_driver ='Y000' and oi.state='S616' where oi.state ='S299'
and oi.company_id = #{companyId}
and oi.car_id in ( select car_id from car_info where car_group_id in (select car_group_id from car_group_dispatcher_info where user_id=#{userId}) )

select
      oi.order_id
from order_info oi
    where oi.state='S900'
     and oi.create_time >= #{beginDate}
     and oi.create_time <= #{endDate}
    and oi.car_id in (
        select  ci.car_id from car_info ci where ci.car_group_id=#{carGroupId}
    )

select start_date,GROUP_CONCAT(order_state_trace_info.state)states,order_info.* from order_info
left join order_state_trace_info on order_info.order_id=order_state_trace_info.order_id
left join journey_info on journey_info.journey_id = order_info.journey_id
where date(start_date) >= #{beginDate} and date(start_date) <= #{endDate}
and order_info.company_id = #{companyId}
group by order_info.order_id

select order_id from order_info where journey_id = #{journeyId}
order By order_id desc
limit 1

select s.company_id from order_info s left join order_dispatche_detail_info oodi on oodi.order_id = s.order_id LEFT join journey_user_car_power jucp on jucp.power_id=s.power_id LEFT join apply_info a on a.apply_id=jucp.apply_id LEFT join ecmp_user eu on eu.user_id=a.create_by LEFT join journey_info ji on ji.journey_id=s.journey_id LEFT join regime_info ri on ri.regimen_id=ji.regimen_id LEFT join journey_passenger_info jpi on jpi.journey_id=s.journey_id LEFT join order_settling_info osi on osi.order_id=s.order_id left join order_address_info oai on oai.order_id=s.order_id and oai.type='A000' left join order_address_info oai2 on oai2.order_id=s.order_id and oai2.type='A999' left join ecmp_org eo on eu.dept_id = eo.dept_id left join driver_info di on di.driver_id=s.driver_id left join car_info ci on ci.car_id =s.car_id left join car_group_info cgi on cgi.car_group_id = ci.car_group_id
and s.state = (select state from order_state_trace_info sti where sti.order_id=s.order_id and s.state = #{state} order by trace_id desc limit 1)
and oodi.it_is_self_driver='Y000'
and s.company_id = #{companyId}
and eu.dept_id = #{deptId}
and s.car_id in ( select car_id from car_info where car_group_id in (select car_group_id from car_group_dispatcher_info where user_id=#{updateBy}) )

SELECT oi.order_id orderId, -- 订单id osti.state state, -- 订单状态轨迹表的状态 eo.dept_name deptName, -- 部门表的部门 ji.start_date beginTime, -- 行程表的开始时间 用车时间 jpi.name vehicleUserName, -- 乘客信息表的 用车人 ecti.name carName, -- 企业车型表的 车型名字 ci.car_license carLicense, -- 车辆表的 车牌号 oai1.address beginAddress, -- 订单地址信息表的 出发地址 oai2.address endAddress, -- 订单地址信息表的 到达地址 ji.use_time useTime -- 行程表的用车天数 FROM order_info oi -- 订单表 LEFT JOIN (SELECT state,order_id,create_time -- 状态，订单id FROM `order_state_trace_info` -- 订单轨迹表 WHERE trace_id IN ( SELECT MAX(trace_id) FROM order_state_trace_info GROUP BY order_id -- 按订单id查询最大的订单轨迹 id ) ) -- 在轨迹表中查询所有订单最大的轨迹 id 数据列表中的 -- 轨迹状态 和 订单id osti ON osti.order_id=oi.order_id LEFT JOIN journey_info ji ON oi.journey_id = ji.journey_id -- 行程表 行程id = 订单表 行程id LEFT JOIN driver_info di ON di.driver_id = oi.driver_id -- 驾驶员表的 司机id = 订单表的 司机id LEFT JOIN journey_passenger_info jpi ON jpi.journey_id = ji.journey_id -- 乘客信息表的 行程id = 行程表的 行程id LEFT JOIN ecmp_user eu ON eu.user_id = oi.user_id -- 用户表的 用户id = 订单标的 用户id LEFT JOIN ecmp_org eo ON eo.dept_id = eu.dept_id -- 部门表的 部门id = 用户表的 部门id LEFT JOIN car_info ci ON ci.car_id = oi.car_id -- 车辆表的 车辆id = 订单表的 车辆id LEFT JOIN enterprise_car_type_info ecti ON ecti.car_type_id = ci.car_type_id -- 车型标表的 车型id = 车辆表的 车型id LEFT JOIN car_group_driver_relation cgdr ON cgdr.driver_id = oi.driver_id -- 车队司机关联表的 司机id = 订单吧表的 司机id LEFT JOIN car_group_info cgi ON cgi.car_group_id = cgdr.car_group_id -- 车队表的 车队id = 车队司机关联表的 车队id LEFT JOIN (SELECT order_id,TYPE,address,address_long FROM order_address_info WHERE TYPE = 'A000') oai1 -- 从订单地址信息表查询 订单id，A000,真实出发地址，真实出发长地址 ON oai1.order_id = oi.order_id LEFT JOIN (SELECT order_id,TYPE,address,address_long FROM order_address_info WHERE TYPE = 'A999') oai2 -- 从订单地址信息表查询 订单id，A999,真实到达地址，真实到达长地址 ON oai2.order_id = oi.order_id WHERE osti.state IN ('S900','S901') -- 订单状态轨迹表的状态为 S901,S900
and eu.dept_id = #{deptId}
and osti.create_time >= #{beginDate}
and osti.create_time <= #{endDate}
-- and cgi.it_is_inner in ('C000','C111') and cgi.car_group_name = #{carGroupName}
