mapper.select:


     and journey_id = #{journeyId}
     and node_id = #{nodeId}
     and power_id = #{powerId}
     and driver_id = #{driverId}
     and car_id = #{carId}
     and use_car_mode = #{useCarMode}
     and state = #{state}
     and actual_setout_address = #{actualSetoutAddress}
     and actual_setout_longitude = #{actualSetoutLongitude}
     and actual_setout_latitude = #{actualSetoutLatitude}
     and actual_setout_time = #{actualSetoutTime}
     and actual_arrive_address = #{actualArriveAddress}
     and actual_arrive_longitude = #{actualArriveLongitude}
     and actual_arrive_latitude = #{actualArriveLatitude}
     and actual_arrive_time = #{actualArriveTime}
     and tripartite_order_id = #{tripartiteOrderId}
     and tripartite_platform_code = #{tripartitePlatformCode}
     and driver_name like concat('%', #{driverName}, '%')
     and driver_mobile = #{driverMobile}
     and car_license = #{carLicense}

order by actualSetoutTime desc

mapper.update:
update order_info
journey_id = #{journeyId}, node_id = #{nodeId}, power_id = #{powerId}, driver_id = #{driverId}, car_id = #{carId}, use_car_mode = #{useCarMode}, state = #{state}, actual_setout_address = #{actualSetoutAddress}, actual_setout_longitude = #{actualSetoutLongitude}, actual_setout_latitude = #{actualSetoutLatitude}, actual_setout_time = #{actualSetoutTime}, actual_arrive_address = #{actualArriveAddress}, actual_arrive_longitude = #{actualArriveLongitude}, actual_arrive_latitude = #{actualArriveLatitude}, actual_arrive_time = #{actualArriveTime}, tripartite_order_id = #{tripartiteOrderId}, tripartite_platform_code = #{tripartitePlatformCode}, driver_name = #{driverName}, driver_mobile = #{driverMobile}, car_license = #{carLicense}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where order_id = #{orderId}

mapper.select:
select o.order_id orderId, o.state, if (o.state='S900','已完成','未完成') stateType, jn.service_type serviceType, jn.flight_number flightNumber, jn.it_is_return itIsReturn, jn.charter_car_type charterCarType, IFNULL(o.actual_setout_time,jni.plan_setout_time) useCarTime, IFNULL(o.actual_setout_address,jni.plan_begin_address) startAddr, IFNULL(o.actual_arrive_address,jni.plan_end_address) endAddr, otc.state labelState from order_info o inner join journey_info jn on o.journey_id=jn.journey_id inner join journey_node_info jni on jni.node_id=o.node_id left join (select order_id,state from order_state_trace_info where order_id in(select order_id from order_info where driver_id=#{driverId}) order by create_time desc limit 1) otc on otc.order_id=o.order_id where o.driver_id=#{driverId}
and (o.state="S299" or o.state="S600" or o.state="S616" ) order by useCarTime asc
and (o.state='S299' or o.state='S600' or o.state='S616' or o.state='S900' ) order by o.state='S900' asc,o.state asc, CASE WHEN o.state='S900' THEN o.update_time END DESC,useCarTime asc

SELECT messageId,messageType,messageTypeStr,count(*) messageCount from ( select order_id as messageId
,4 as messageType,'派车'as messageTypeStr
,1 as messageType,'新任务'as messageTypeStr
from order_info where find_in_set(state,#{states})
and driver_id=#{driveId}
) a group by a.messageType

SELECT messageId,messageType,'取消任务' messageTypeStr,count(*) messageCount from (
   select order_id as messageId,4 as messageType from order_info
   where state=#{state} and driver_id=#{driverId}
   ) a group by a.messageType
