mapper.update:
update order_info
journey_id = #{journeyId}, node_id = #{nodeId}, power_id = #{powerId}, driver_id = #{driverId}, car_id = #{carId}, user_id = #{userId}, use_car_mode = #{useCarMode}, service_type = #{serviceType}, state = #{state}, tripartite_order_id = #{tripartiteOrderId}, tripartite_platform_code = #{tripartitePlatformCode}, driver_name = #{driverName}, driver_mobile = #{driverMobile}, car_license = #{carLicense}, flight_number = #{flightNumber}, demand_car_level = #{demandCarLevel}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, order_number = #{orderNumber},
where order_id = #{orderId}

mapper.select:
SELECT a.order_id AS orderId, a.state, d.apply_type AS applyType, b.type AS serverType, c.plan_begin_address AS useCarCity, e.nick_name AS applyUserName, e.nick_name AS useCarUserName,
h.use_car_mode AS useCarMode, h.regimen_id AS regimenId ,f.content as reassignmentApplyReason FROM order_info a LEFT JOIN journey_user_car_power b ON a.power_id = b.power_id LEFT JOIN journey_node_info c ON a.node_id = c.node_id LEFT JOIN journey_info h ON h.journey_id=a.journey_id LEFT JOIN apply_info d ON b.apply_id = d.apply_id LEFT JOIN ecmp_user e ON e.user_id = c.user_id LEFT JOIN order_state_trace_info f on a.order_id=f.order_id
and a.state = #{state} and f.state = #{orderTraceState}

select o.order_id orderId, o.state, if (o.state='S900','已完成','未完成') stateType, o.service_type serviceType, o.flight_number flightNumber, jn.it_is_return itIsReturn, jn.charter_car_type charterCarType, s.action_time useCarTime, s.address startAddr, t.address endAddr, otc.state labelState from order_info o inner join journey_info jn on o.journey_id=jn.journey_id inner join order_address_info s on s.order_id=o.order_id and s.type='A000' inner join order_address_info t on t.order_id=o.order_id and t.type='A999' left join (select order_id,state from order_state_trace_info where order_id in(select order_id from order_info where driver_id=#{driverId}) order by create_time desc limit 1) otc on otc.order_id=o.order_id where o.driver_id=#{driverId}
and (o.state="S299" or o.state="S600" or o.state="S616" ) order by useCarTime asc
and (o.state='S299' or o.state='S600' or o.state='S616' or o.state='S900' ) order by o.state='S900' asc,o.state asc, CASE WHEN o.state='S900' THEN o.update_time END DESC,useCarTime asc

SELECT messageId,messageType,messageTypeStr,count(*) messageCount from ( select order_id as messageId
,4 as messageType,'派车'as messageTypeStr
,1 as messageType,'新任务'as messageTypeStr
from order_info where find_in_set(state,#{states})
and driver_id=#{driveId}
) a group by a.messageType

SELECT messageId,messageType,'取消任务' messageTypeStr,count(*) messageCount from (
   select order_id as messageId,4 as messageType from order_info
   where state=#{state} and driver_id=#{driverId}
   ) a group by a.messageType

SELECT a.order_id AS orderId, b.nick_name AS applyUserName, b.user_name AS applyUserMobile, a.journey_id AS journeyId, a.node_id AS nodeId, a.update_time AS optDate, a.state AS state, a.service_type as serviceType FROM order_state_trace_info d LEFT JOIN order_info a ON a.order_id = d.order_id LEFT JOIN ecmp_user b ON a.user_id = b.user_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state NOT IN ('S270', 'S277', 'S279') AND a.state in ('S199','S200','S299','S600','S616','S699','S900')
and b.user_name LIKE concat('%',#{applyMobile},'%')
and AND b.nick_name LIKE concat('%',#{applyName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id
LIMIT #{offset},#{pageSize}

SELECT count(1) FROM order_state_trace_info d LEFT JOIN order_info a ON a.order_id = d.order_id LEFT JOIN ecmp_user b ON a.user_id = b.user_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state NOT IN ('S270', 'S277', 'S279') AND a.state in ('S199','S200','S299','S600','S616','S699','S900')
and b.user_name LIKE concat('%',#{applyMobile},'%')
and AND b.nick_name LIKE concat('%',#{applyName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id

SELECT a.order_id AS orderId, b.nick_name AS applyUserName, b.user_name AS applyUserMobile, a.journey_id AS journeyId, a.node_id AS nodeId, a.update_time AS optDate, a.state AS state FROM order_state_trace_info d LEFT JOIN order_info a ON a.order_id = d.order_id LEFT JOIN driver_info b ON a.driver_id = b.driver_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state IN ('S270', 'S277', 'S279') and a.state in ('S199','S200','S299','S600','S616','S699','S900')
and b.mobile LIKE concat('%',#{driverMobile},'%')
and AND b.driver_name LIKE concat('%',#{driverName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id
LIMIT #{offset},#{pageSize}

SELECT count(1) FROM order_state_trace_info d LEFT JOIN order_info a ON a.order_id = d.order_id LEFT JOIN driver_info b ON a.driver_id = b.driver_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state IN ('S270', 'S277', 'S279') and a.state in ('S199','S200','S299','S600','S616','S699','S900')
and b.mobile LIKE concat('%',#{driverMobile},'%')
and AND b.driver_name LIKE concat('%',#{driverName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id
