mapper.select:
SELECT messageId,messageType,messageTypeStr,count(*) messageCount from ( select order_id as messageId
,4 as messageType,'派车'as messageTypeStr
,1 as messageType,'新任务'as messageTypeStr
from order_info where find_in_set(state,#{states})
and driver_id=#{driveId}
) a group by a.messageType

SELECT messageId,messageType,'取消任务' messageTypeStr,count(*) messageCount from (
   select order_id as messageId,4 as messageType from order_info
   where state=#{state} and driver_id=#{driverId}
   ) a group by a.messageType

SELECT a.order_id AS orderId, b.nick_name AS applyUserName, b.user_name AS applyUserMobile, a.journey_id AS journeyId, a.node_id AS nodeId, a.update_time AS optDate, a.state AS state, a.service_type as serviceType, h.it_is_return as itIsReturn, h.wait_time_long as waitTimeLong, f.SHORT_NAME as cityName FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN ecmp_user b ON a.user_id = b.user_id LEFT JOIN order_address_info c ON a.order_id = c.order_id LEFT JOIN journey_info h on a.journey_id=h.journey_id LEFT JOIN china_city f ON c.city_postal_code =f.CITY_CODE WHERE d.state NOT IN ('S270', 'S277', 'S279') AND a.state in ('S100','S199','S299','S500','S600','S616','S699','S900') and (a.use_car_mode is null or a.use_car_mode='W100' or a.use_car_mode='' or a.use_car_mode='W200') and a.company_id=#{companyId}
and b.user_name LIKE concat('%',#{applyMobile},'%')
AND b.nick_name LIKE concat('%',#{applyName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id

select count(1) from (SELECT count(1) FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN ecmp_user b ON a.user_id = b.user_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state NOT IN ('S270', 'S277', 'S279') AND a.state in ('S100','S199','S299','S500','S600','S616','S699','S900') and (a.use_car_mode is null or a.use_car_mode='W100' or a.use_car_mode='')
and b.user_name LIKE concat('%',#{applyMobile},'%')
AND b.nick_name LIKE concat('%',#{applyName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id ) m

SELECT a.order_id AS orderId, b.driver_name AS applyUserName, b.mobile AS applyUserMobile, a.journey_id AS journeyId, a.node_id AS nodeId, a.update_time AS optDate, a.state AS state, h.it_is_return as itIsReturn, h.wait_time_long as waitTimeLong, f.SHORT_NAME as cityName FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN driver_info b ON a.driver_id = b.driver_id LEFT JOIN order_address_info c ON a.order_id = c.order_id LEFT JOIN journey_info h on a.journey_id=h.journey_id LEFT JOIN china_city f ON c.city_postal_code =f.CITY_CODE WHERE d.state IN ('S270', 'S277', 'S279') and a.state in ('S299','S500','S600','S616','S900','S699') and a.use_car_mode is not null
and b.mobile LIKE concat('%',#{driverMobile},'%')
AND b.driver_name LIKE concat('%',#{driverName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id

select count(1) from (SELECT count(1) FROM order_info a LEFT JOIN order_state_trace_info d ON a.order_id = d.order_id LEFT JOIN driver_info b ON a.driver_id = b.driver_id LEFT JOIN order_address_info c ON a.order_id = c.order_id WHERE d.state IN ('S270', 'S277', 'S279') and a.state in ('S299','S500','S600','S616','S900','S699') and a.use_car_mode is not null
and b.mobile LIKE concat('%',#{driverMobile},'%')
AND b.driver_name LIKE concat('%',#{driverName},'%')
AND c.city_postal_code = #{cityCode}
AND a.update_time >= #{optStartDate}
AND a.update_time <= #{optEndDate}
AND a.use_car_mode = #{useCarMode}
GROUP BY d.order_id ) m

SELECT
   ord.*
FROM  order_info ord
LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id

     and  ord.car_id=#{carId}  
     and jpp.planned_arrival_time > #{setOutTime}  
     and ord.driver_id = #{driverId}  

ORDER BY  ord.order_id DESC

SELECT ord.* FROM order_info ord LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id
and ord.car_id = #{carId} and #{arrivalTime} > jpp.planned_departure_time and ord.driver_id = #{driverId}
ORDER BY ord.order_id DESC

SELECT
    ord.order_id,
    jpp.planned_departure_time as create_time
FROM  order_info ord
LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id

     and  ord.car_id=#{carId}  
     and jpp.planned_arrival_time >#{setOutTime}  
     and ord.driver_id = #{driverId}

ORDER BY  ord.order_id DESC

SELECT ord.order_id, jpp.planned_arrival_time as update_time FROM order_info ord LEFT JOIN journey_plan_price_info jpp on jpp.node_id=ord.node_id and jpp.journey_id=ord.journey_id
and ord.car_id=#{carId} and #{arrivalTime} > jpp.planned_departure_time and ord.driver_id = #{driverId}
ORDER BY ord.order_id DESC

select order_id orderId,state,use_car_mode useCarMode,
(select state from order_state_trace_info sti where sti.order_id=#{orderId} order by trace_id desc limit 1) labelState
from  order_info where order_id = #{orderId}

SELECT count(*) FROM order_info where state='S100'
 and user_id in

   #{userId}

SELECT count(*) FROM order_info where user_id in
#{userId}
AND order_id IN (SELECT order_id from ( SELECT order_id ,state FROM ( SELECT order_id, state FROM order_state_trace_info ORDER BY trace_id DESC) r GROUP BY r.order_id having r.state = 'S279' ) tmp)

select scene_id from scene_regime_relation where regimen_id=(
    SELECT regimen_id FROM `journey_info` where journey_id=#{journeyId}
)
