mapper.update:
update regime_info
template_id = #{templateId}, name = #{name}, allow_city = #{allowCity}, rule_city = #{ruleCity}, allow_time = #{allowTime}, setout_address = #{setoutAddress}, arrive_address = #{arriveAddress}, allow_date = #{allowDate}, setout_equal_arrive = #{setoutEqualArrive}, service_type = #{serviceType}, approval_process = #{approvalProcess}, project_need = #{projectNeed}, can_use_car_mode = #{canUseCarMode}, can_use_car_level = #{canUseCarLevel}, allow_date_round_travel = #{allowDateRoundTravel}, allow_city_round_travel = #{allowCityRoundTravel}, state = #{state}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, cost_center=#{costCenter}, limit_type=#{limitType}, limit_money=#{limitMoney}, travel_city_use_car_limit_type=#{travelCityUseCarLimitType}, travel_city_use_car_limit_money=#{travelCityUseCarLimitMoney}, travel_limit_type=#{travelLimitType}, travel_limit_money=#travelLimitMoney}, create_by,
where regimen_id = #{regimenId}

mapper.select:
SELECT a.regimen_id AS regimeId, a. NAME AS regimeName, c. NAME AS sceneName, a.approve_template_id AS approveTemplateId, a. state AS state, a.need_approval_process as needApprovalProcess FROM regime_info a LEFT JOIN scene_regime_relation b ON a.regimen_id = b.regimen_id LEFT JOIN scene_info c ON b.scene_id = c.scene_id WHERE a.state !='N111'
and c.scene_id = #{sceneId}
and a.state =#{state}
and a.NAME like CONCAT('%',#{regimeName},'%')
and a.company_id = #{companyId}
group by a.regimen_id

select count(1) from (SELECT count(1) FROM regime_info a LEFT JOIN scene_regime_relation b ON a.regimen_id = b.regimen_id LEFT JOIN scene_info c ON b.scene_id = c.scene_id WHERE a.state !='N111'
and c.scene_id = #{sceneId}
and a.state =#{state}
and a.NAME like CONCAT('%',#{regimeName},'%')
group by a.regimen_id ) m

SELECT  use_car_mode_online_level
FROM regime_info
WHERE regimen_id = #{regimenId}
